import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar, Line, Doughnut, Pie } from 'react-chartjs-2';
import { API_URL } from '../services/api';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, ArcElement, Title, Tooltip, Legend } from 'chart.js';

// Register ChartJS components
ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, ArcElement, Title, Tooltip, Legend);

const AnalyticsDashboard = () => {
    const [analyticsData, setAnalyticsData] = useState({
        jobsPerLocation: [],
        inDemandSkills: [],
        averageSalary: [],
        applicationTrends: []
    });

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [timeFilter, setTimeFilter] = useState('all'); // 'week', 'month', 'year', 'all'

    useEffect(() => {
        const fetchAnalyticsData = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`${API_URL}/jobs/analytics/summary?timeframe=${timeFilter}`);
                setAnalyticsData(response.data);
                setError(null);
            } catch (error) {
                console.error('Error fetching analytics data:', error);
                setError('Failed to load analytics data. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        fetchAnalyticsData();
    }, [timeFilter]);

    // Generate colors for charts
    const generateColors = (count) => {
        const colors = [
            'rgba(54, 162, 235, 0.6)', 'rgba(255, 99, 132, 0.6)', 
            'rgba(255, 206, 86, 0.6)', 'rgba(75, 192, 192, 0.6)',
            'rgba(153, 102, 255, 0.6)', 'rgba(255, 159, 64, 0.6)',
            'rgba(199, 199, 199, 0.6)', 'rgba(83, 102, 255, 0.6)',
            'rgba(78, 205, 196, 0.6)', 'rgba(255, 99, 71, 0.6)'
        ];
        return Array.from({ length: count }, (_, i) => colors[i % colors.length]);
    };

    const renderJobsPerLocationChart = () => {
        if (!analyticsData.jobsPerLocation?.length) return <div className="no-data">No location data available</div>;

        const labels = analyticsData.jobsPerLocation.map(item => item._id);
        const dataVals = analyticsData.jobsPerLocation.map(item => item.count);
        const backgroundColor = generateColors(labels.length);

        const data = {
            labels,
            datasets: [
                {
                    label: 'Jobs Posted per Location',
                    data: dataVals,
                    backgroundColor,
                    borderColor: backgroundColor.map(color => color.replace('0.6', '1')),
                    borderWidth: 1
                },
            ],
        };

        const options = {
            responsive: true,
            plugins: {
                legend: { position: 'bottom' },
                title: { display: true, text: 'Job Distribution by Location' }
            }
        };

        return <Bar data={data} options={options} />;
    };

    const renderInDemandSkillsChart = () => {
        if (!analyticsData.inDemandSkills?.length) return <div className="no-data">No skills data available</div>;

        const labels = analyticsData.inDemandSkills.map(item => item._id);
        const dataVals = analyticsData.inDemandSkills.map(item => item.count);
        const backgroundColor = generateColors(labels.length);

        const data = {
            labels,
            datasets: [
                {
                    label: 'Demand Count',
                    data: dataVals,
                    backgroundColor,
                    borderColor: backgroundColor.map(color => color.replace('0.6', '1')),
                    borderWidth: 1
                },
            ],
        };

        const options = {
            responsive: true,
            plugins: {
                legend: { position: 'bottom' },
                title: { display: true, text: 'Most In-Demand Skills' }
            }
        };

        return <Doughnut data={data} options={options} />;
    };

    // Sample application trends chart (would need backend support)
    const renderApplicationTrendsChart = () => {
        // This assumes you have application date data from backend
        // If not available, show placeholder

        // Placeholder data - in real implementation, use data from API
        const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
        const data = {
            labels: months,
            datasets: [
                {
                    label: 'Applications',
                    data: [65, 59, 80, 81, 56, 55, 40, 50, 60, 70, 85, 90],
                    fill: false,
                    borderColor: 'rgb(75, 192, 192)',
                    tension: 0.1
                }
            ]
        };

        const options = {
            responsive: true,
            plugins: {
                legend: { position: 'bottom' },
                title: { display: true, text: 'Application Trends' }
            }
        };

        return <Line data={data} options={options} />;
    };

    // Salary ranges by job title
    const renderAverageSalaryChart = () => {
        // This assumes you have salary data from backend
        // If not available, show placeholder

        // Placeholder data - in real implementation, use data from API
        const jobTitles = ['Software Engineer', 'Data Scientist', 'Product Manager', 'UX Designer', 'DevOps Engineer'];
        const data = {
            labels: jobTitles,
            datasets: [
                {
                    label: 'Avg. Min Salary',
                    data: [70000, 85000, 90000, 65000, 80000],
                    backgroundColor: 'rgba(54, 162, 235, 0.6)',
                },
                {
                    label: 'Avg. Max Salary',
                    data: [120000, 140000, 150000, 110000, 130000],
                    backgroundColor: 'rgba(255, 99, 132, 0.6)',
                }
            ]
        };

        const options = {
            responsive: true,
            plugins: {
                legend: { position: 'bottom' },
                title: { display: true, text: 'Average Salary Ranges by Job Title' }
            },
            scales: {
                y: {
                    beginAtZero: true,
                    title: {
                        display: true,
                        text: 'Salary (USD)'
                    }
                }
            }
        };

        return <Bar data={data} options={options} />;
    };

    if (loading) return (
        <div className="analytics-loading">
            <div className="spinner"></div>
            <p>Loading analytics data...</p>
        </div>
    );

    if (error) return (
        <div className="analytics-error">
            <p>{error}</p>
            <button onClick={() => setTimeFilter(timeFilter)}>Retry</button>
        </div>
    );

    return (
        <div className="analytics-dashboard">
            <div className="analytics-header">
                <h1>Analytics Dashboard</h1>
                <div className="filters">
                    <label>Time Period:</label>
                    <select value={timeFilter} onChange={(e) => setTimeFilter(e.target.value)}>
                        <option value="all">All Time</option>
                        <option value="week">Past Week</option>
                        <option value="month">Past Month</option>
                        <option value="year">Past Year</option>
                    </select>
                </div>
            </div>

            <div className="analytics-summary">
                <div className="summary-card">
                    <h3>Total Jobs</h3>
                    <p className="summary-number">
                        {analyticsData.jobsPerLocation?.reduce((sum, item) => sum + item.count, 0) || 0}
                    </p>
                </div>
                <div className="summary-card">
                    <h3>Unique Skills</h3>
                    <p className="summary-number">{analyticsData.inDemandSkills?.length || 0}</p>
                </div>
                <div className="summary-card">
                    <h3>Locations</h3>
                    <p className="summary-number">{analyticsData.jobsPerLocation?.length || 0}</p>
                </div>
                <div className="summary-card">
                    <h3>Avg. Job Applications</h3>
                    <p className="summary-number">12</p> {/* Placeholder - would come from backend */}
                </div>
            </div>

            <div className="charts-container">
                <div className="chart-card">
                    <h2>Jobs by Location</h2>
                    {renderJobsPerLocationChart()}
                </div>
                <div className="chart-card">
                    <h2>In-Demand Skills</h2>
                    {renderInDemandSkillsChart()}
                </div>
                <div className="chart-card">
                    <h2>Application Trends</h2>
                    {renderApplicationTrendsChart()}
                </div>
                <div className="chart-card">
                    <h2>Salary Analysis</h2>
                    {renderAverageSalaryChart()}
                </div>
            </div>
        </div>
    );
};

export default AnalyticsDashboard;
